def solve(s):
    letters = [c for c in s if c.isalpha()]

    if not letters:
        return s[::-1]

    result = ""
    for c in s:
        if c.isalpha():
            if c.islower():
                result += c.upper()
            else:
                result += c.lower()
        else:
            result += c

    return result

